import vibe.core.net;
import vibe.stream.ssl;

void sendSSLMessage()
{
	auto conn = connectTCP("127.0.0.1", 1234);
	auto sslctx = createSSLContext(SSLContextKind.client);
	auto stream = createSSLStream(conn, sslctx);
	stream.write("Hello, World!");
	stream.finalize();
	conn.close();
}

