// using 'with' statement
with (r.route("/foo")) {
	get(&getFoo);
	post(&postFoo);
	delete_(&deleteFoo);
}

// using method chaining
r.route("/foo")
	.get(&getFoo)
	.post(&postFoo)
	.delete_(&deleteFoo);

